enter;\n  font-weight: 400;\n  line-height: 75px;\n  margin: 0;\n}\n\n.blue-text{\n  color: #007ee5;\n}\n\n.block-subtitle{\n  font-size: 21px;\n  color: #7e7e7e;\n  line-height: 29px;\n  width: 100%;\n  max-width: 650px;\n  text-align: center;\n  margin: 0 auto;\n  font-weight: 300;\n  margin-bottom: 70px;\n}",".header{\n  height: 100px;\n  width: 100%;\n  position: absolute;\n  z-index: 99999;\n  transition: top 1s;\n  &.js-show{\n    position: fixed;\n    top: 0;\n    background: rgba(0,0,0,0.6);\n  }\n  &.js-hide{\n    position: fixed;\n    background: rgba(0,0,0,0.6);\n    top: -100px;\n  }\n  .container{\n    justify-content: space-between;\n  }\n}\n\n.header__logo{\n  display: flex;\n  align-items: center;\n  transition: transform 1s;\n  &:hover{\n    transform: rotate(360deg);\n  }\n}\n\n.header-nav{\n  display: flex;\n  align-items: center;\n  height: 100%;\n}\n\n.header-nav__item{\n  margin: 0 12px;\n  &--button{\n    margin: 0 0 0 83px;\n  }\n}\n\n.header-nav__link{\n  font-size: 14px;\n  text-transform: uppercase;\n  color: #fff;\n  line-height: 24px;\n  text-decoration: none;\n}\n\n.header-nav__button{\n  @include button(38px, #006bc3)\n}","@mixin button($height, $bgColor){\n  padding: 22px;\n  background: $bgColor;\n  border-radius: $height/2;\n  display: flex;\n  justify-content: center;\n  align-items: center;\n  height: $height;\n  padding: 0 $height/3;\n  color: #fff;\n  cursor: pointer;\n  outline: none;\n  text-transform: uppercase;\n  font-size: 14px;\n}\n\n@mixin gradient($deg, $firstColor, $secondColor){\n  background: linear-gradient($deg, $firstColor, $secondColor);\n}\n",".hero-main{\n  width: 100%;\n  min-height: 100vh;\n  background: #007ee5;\n  padding-top: 170px;\n  .container{\n    flex-direction: column;\n  }\n}\n\n.hero-main__title{\n  font-size: 56px;\n  color: #fff;\n  text-align: center;\n  font-weight: 400;\n  line-height: 75px;\n  margin: 0;\n}\n\n.hero-main__subtitle{\n  font-size: 26px;\n  color: #fff;\n  text-align: center;\n  line-height: 46px;\n}\n\n.hero-main__button{\n  @include button(50px, transparent);\n  border: solid 1px #fff;\n  width: 270px;\n  margin: 30px auto;\n}\n\n.hero-main__image-container{\n  position: relative;\n  height: 500px;\n  width: 975px;\n  margin: 0 auto;\n  img{\n    position: absolute;\n    &:first-child{\n      top: 0;\n      left: 40px;\n    }\n    &:last-child{\n      top: 0;\n      right: 40px;\n    }\n  }\n}",".apocalypse-development{\n  background: #1f1f1f;\n  padding-top: 110px;\n  .container{\n    flex-direction: column;\n  }\n}\n\n.apocalypse-development--center{\n  .apocalypse-development__point{\n    align-items:  center;\n  }\n  .apocalypse-development__point-title{\n    text-align: center;\n  }\n  .apocalypse-development__point-lead{\n    text-align: center;\n  }\n}\n\n.apocalypse-development__subtitle{\n  font-size: 21px;\n  color: #7e7e7e;\n  line-height: 29px;\n  width: 100%;\n  max-width: 650px;\n  text-align: center;\n  margin: 0 auto;\n  font-weight: 300;\n  margin-bottom: 70px;\n}\n\n.apocalypse-development__title{\n  margin-bottom: 25px;\n}\n\n.apocalypse-development__points-container{\n  display: flex;\n  flex-wrap: wrap;\n  width: 100%;\n  justify-content: space-between;\n}\n\n.apocalypse-development__point{\n  width: 50%;\n  display: flex;\n  flex-direction: column;\n  justify-content: flex-start;\n  max-width: 490px;\n  margin-bottom: 100px;\n  .apocalypse-development__point-image{\n    width: 100px;\n    height: 100px;\n    background-size: contain;\n    background-position: center;\n    background-repeat: no-repeat;\n  }\n}\n\n.apocalypse-development__point-title{\n  color: #fff;\n  font-size: 18px;\n  line-height: 21px;\n  width: 100%;\n  max-width: 265px;\n  font-weight: 300;\n  margin: 20px 0;\n}\n\n.apocalypse-development__point-lead{\n  font-size: 14px;\n  color: #7e7e7e;\n  margin: 0;\n  line-height: 22px;\n}\n\n.apocalypse-development__button{\n  @include button(50px, #017ade);\n  border: none;\n  max-width: 200px;\n  margin: auto;\n  margin-bottom: 120px;\n}",".switcher{\n  display: flex;\n  justify-content: space-around;\n  align-items: center;\n  height: 44px;\n  width: 135px;\n  border-radius: 22px;\n  position: relative;\n  background: #fff;\n\n  .swithcher-selected{\n    position: relative;\n    z-index: 2;\n    transition: all 0.3s;\n    &.no{\n      color: #fff;\n    }\n    &.yes{\n      color: #1a1a1a;\n    }\n  }\n\n  &.checked{\n    .swithcher-selected{\n      &.yes{\n        color: #fff;\n      }\n      &.no{\n        color: #1a1a1a;\n      }\n    }\n    &:before{\n      transform: translateX(-67px);\n    }\n  }\n\n  &:before{\n    z-index: 1;\n    display: block;\n    content: '';\n    position: absolute;\n    background: #007ee5;\n    width: 57px;\n    height: 32px;\n    border-radius: 16px;\n    top: 6px;\n    left: auto;\n    right: 6px;\n    transition: all 0.3s;\n  }\n\n  input{\n    display: none;\n  }\n}\n",".idea-tabs{\n  background: #1f1f1f;\n  padding-top: 150px;\n  padding-bottom: 100px;\n  .container{\n    flex-direction: column;\n  }\n}\n\n.idea-tabs__title-area{\n  display: flex;\n  justify-content: center;\n  align-items: center;\n  width: 100%;\n}\n\n.idea-tabs__title{\n  margin-right: 60px;\n  margin-bottom: 20px;\n}\n\n.idea-tabs__tab{\n  display: flex;\n}\n\n.idea-tabs__tab-picture{\n  width: 100%;\n  max-width: 300px;\n  height: 270px;\n  margin: 0 47px;\n  img{\n    width: 100%;\n    height: 100%;\n    object-fit: contain;\n  }\n}\n\n.idea-tabs__tab-list{\n  list-style: none;\n  padding-top: 50px;\n  margin: 0;\n}\n\n.idea-tabs__list-item{\n  color: #fff;\n  font-size: 14px;\n  position: relative;\n  padding: 10px 0;\n  padding-left: 23px;\n  &:before{\n    content: '';\n    position: absolute;\n    display: block;\n    border-radius: 50%;\n    top: 0;\n    bottom: 0;\n    left: 0;\n    margin: auto;\n    background: #616161;\n    width: 6px;\n    height: 6px;\n    \n  }\n}\n\n.idea-tabs__subtitle{\n  width: 100%;\n  max-width: none;\n}",".gradient-b